package main

import "fmt"

func numberOfWays(corridor string) int {
	res := 1
	mod := 1000000007

	count := 0
	idx1, idx2 := 0, 0
	for i, elem := range corridor {
		if elem == 'S' {
			count++
			if count == 2 {
				idx1 = i
				break
			}
		}
	}

	if count == 0 || count == 1 {
		return 0
	}

	for i := idx1 + 1; i < len(corridor); i++ {
		if corridor[i] == 'S' {
			count++

			if count%2 == 0 {
				res = (res * (idx2 - idx1)) % mod
				idx1 = i
			} else {
				idx2 = i
			}
		}
	}

	if count%2 != 0 {
		return 0
	}

	return res
}

func main() {
	corridor := "SSPPSPS"
	fmt.Println(numberOfWays(corridor))

	corridor1 := "SPPSSSSPPS"
	fmt.Println(numberOfWays(corridor1))

	corridor2 := "SSS"
	fmt.Println(numberOfWays(corridor2))

	corridor3 := ""
	fmt.Println(numberOfWays(corridor3))
}
